property.code:
require.config({
  paths: {
    'xwiki-selectize': "$xwiki.getSkinFile('uicomponents/suggest/xwiki.selectize.js', true)" +
      "?v=$escapetool.url($xwiki.version)"
  }
});

require(['jquery', 'xwiki-selectize'], function($) {
  var settings = {
    load: function(typedText, callback) {
      $.getJSON(new XWiki.Document(XWiki.Model.resolve('TaskManager.TaskPagesSearch', XWiki.EntityType.DOCUMENT, XWiki.currentDocument.getDocumentReference())).getURL('get', 'outputSyntax=plain'), {
        text: typedText
      }).done(callback).fail(callback);
    },
    loadSelected: function(selectedValue, callback) {
      $.getJSON(new XWiki.Document(XWiki.Model.resolve('TaskManager.TaskPagesSearch', XWiki.EntityType.DOCUMENT, XWiki.currentDocument.getDocumentReference())).getURL('get', 'outputSyntax=plain'), {
        text: selectedValue,
        exactMatch: true
      }).done(callback).fail(callback);
    }
  };
  if ($('.suggest-task-pages').length) {
    $('.suggest-task-pages').xwikiSelectize(settings);
  } else {
    $(document).on('shown.bs.modal', '.modal', function () {
      $('.suggest-task-pages').xwikiSelectize(settings);
    });
  }
});
